INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
      (UUID(), 'Titanic', '101-year-old Rose DeWitt Bukater tells the story of her life aboard the Titanic, 84 years later. A young Rose boards the ship with her mother and fiancé. Meanwhile, Jack Dawson and Fabrizio De Rossi win third-class tickets aboard the ship. Rose tells the whole story from Titanic''s departure through to its death—on its first and last voyage—on April 15, 1912.', '/9xjZS2rlVxm8SFx8kPC3aIGCOYQ.jpg', 5.00, 1, 'Drama');
INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
      (UUID(), 'Jaws', 'When the seaside community of Amity finds itself under attack by a dangerous great white shark, the town''s chief of police, a young marine biologist, and a grizzled hunter embark on a desperate quest to destroy the beast before it strikes again.', '/lxM6kqilAdpdhqUl2biYp5frUxE.jpg', 5.00, 1, 'Horror');
INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
