INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
      (UUID(), 'Saw X', 'Between the events of ''Saw'' and ''Saw II'', a sick and desperate John Kramer travels to Mexico for a risky and experimental medical procedure in hopes of a miracle cure for his cancer, only to discover the entire operation is a scam to defraud the most vulnerable. Armed with a newfound purpose, the infamous serial killer returns to his work, turning the tables on the con artists in his signature visceral way through devious, deranged, and ingenious traps.', '/aQPeznSu7XDTrrdCtT5eLiu52Yu.jpg', 5.00, 1, 'Horror');
INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
      (UUID(), 'Ricky Stanicky', 'When three childhood best friends pull a prank gone wrong, they invent the imaginary Ricky Stanicky to get them out of trouble. Twenty years later, the trio still uses the nonexistent Ricky as a handy alibi for their immature behavior. But when their spouses and partners get suspicious and demand to finally meet the fabled Mr. Stanicky, the guilty trio decide to hire a washed-up actor and raunchy celebrity impersonator to bring him to life.', '/cEuX73Q1wpMypB1cr4l3MoMGpL4.jpg', 5.00, 1, 'Comedy');
INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
      (UUID(), 'Get Out', 'Chris and his girlfriend Rose go upstate to visit her parents for the weekend. At first, Chris reads the family''s overly accommodating behavior as nervous attempts to deal with their daughter''s interracial relationship, but as the weekend progresses, a series of increasingly disturbing discoveries lead him to a truth that he never could have imagined.', '/mE24wUCfjK8AoBBjaMjho7Rczr7.jpg', 5.00, 1, 'Mystery');
INSERT INTO vhs_tapes (id, title, description, img_url, price_per_day, quantity_available, genre) VALUES
